'use strict';

var merge = require('@graphql-tools/merge');

function createSchema(config) {
  return config;
}
function config(config) {
  return config;
}
function list(config) {
  return config;
}
function singleton(config) {
  return config;
}
function gql(strings) {
  return strings[0];
}
function graphQLSchemaExtension({
  typeDefs,
  resolvers
}) {
  return schema => merge.mergeSchemas({
    schemas: [schema],
    typeDefs,
    resolvers
  });
}

exports.config = config;
exports.createSchema = createSchema;
exports.gql = gql;
exports.graphQLSchemaExtension = graphQLSchemaExtension;
exports.list = list;
exports.singleton = singleton;
