import _defineProperty from '@babel/runtime/helpers/esm/defineProperty';
import FieldController from '@keystonejs/fields/Controller';

class CloudinaryImageController extends FieldController {
  constructor(...args) {
    super(...args);

    _defineProperty(this, "getQueryFragment", () => `
    ${this.path} {
      id
      path
      filename
      originalFilename
      mimetype
      encoding
      publicUrlTransformed(transformation: { width: "120" crop: "limit" })
    }
  `);

    _defineProperty(this, "serialize", data => {
      const {
        path
      } = this;

      if (!data || !data[path]) {
        // Forcibly return null if empty string
        return null;
      }

      return data[path];
    });

    _defineProperty(this, "getFilterTypes", () => []);
  }

}

export default CloudinaryImageController;
